import UseFetch from "../hooks/UseFetch";
import './Global.css';
import { useState } from 'react';

function Date() {

  const [date, setDate] = useState('');

  const info= UseFetch(`http://localhost:4000/noticiasfecha/${date}`);  
 return (
      <div>
        <form>
          <fieldset>
            <legend>
              Fecha
            </legend>
            <input type="date" onChange={e => setDate(e.target.value)}></input>
          </fieldset>
        </form>
        {info && info.map(r =>
          <article>
              <h2>{r?.titulo}</h2>
              <p>{r?.fecha}</p>
              <img src={r?.foto} alt={r?.foto} />
              <h4>{r?.entradilla}</h4>
              <p>{r?.tema}</p>
              <p>{r?.positivo}</p>
          </article>
        )}
      </div>
    )
  }

export default Date;
