import useFetch from "../hooks/UseFetch";
import './Topic.css';
import { useState } from 'react';

function Topic() {
  const [topic, setTopic] = useState('');
    const info= useFetch(`http://localhost:4000/noticiastema/${topic}`);
    return (
      <div className="Topic">
        <form>
          <fieldset>
            <legend>
              Tema
            </legend>
            <input type="text" placeholder="Tema a buscar..." onChange={e => setTopic(e.target.value)}></input>
          </fieldset>
        </form>
        {info && info.map(r =>
          <article>
              <h2>{r?.titulo}</h2>
              <p>{r?.fecha}</p>
              <img src={r?.foto} alt={r?.foto} />
              <h4>{r?.entradilla}</h4>
              <p>{r?.tema}</p>
              <p>{r?.positivo}</p>
          </article>
        )}
        {!info && 
          <p>No hay noticias en la base de datos</p>
        }
      </div>
    )
  }

export default Topic;
