import UseFetch from "../../hooks/UseFetch"
import Article from "../Article"


function UserArticles({token, id}) {
  const data= UseFetch(`http://localhost:4000/noticiasusuario/${id}`, token) 
return (
    <div className="UserNews">
      {data && data.map(r =>
        <article>
            <h2>{r?.titulo}</h2>
            <p>{r?.fecha}</p>
            <img src={r?.foto} alt={r?.foto} />
            <h4>{r?.entradilla}</h4>
            <p>{r?.tema}</p>
            <p>{r?.positivo}</p>
        </article>
      )}
      {!data && 
        <p>No hay noticias para este usuario</p>
      } 
    </div>
)}


export default UserArticles;
