import { Link } from "react-router-dom";

function Article({data, setArticleId}) {
  setArticleId(data.id)
    return (
      <div className="News">
          <article>
            <Link className="link" text={data?.titulo} to="/noticia">
              <h3>{data?.titulo}</h3>
            </Link>
              <p>{data?.fecha}</p>
              <h4>{data?.entradilla}</h4>
              <p>{data?.positivo}üëç
              {data?.negativo}üëé</p>
              <h5>#{data?.tema}</h5>
            
          </article>
      </div>
    )
  }

export default Article;
