import './UserConf.css'
import { useState } from 'react'


function UserConf({token, id}) {
  console.log(token)
  console.log(id)
  const [data, setData] = useState(null)
  
  const handleLoad = async e => {
    e.preventDefault()
    try{
      const res = await fetch(`http://localhost:4000/noticiasusuario/${id}`, {
        method: 'GET',
        headers: {'Content-Type': 'application/json',
        'Authorization': token},     
      })
      const data = await res.json()
        if (res.ok){
          setData(data[0])
        } 
    }   
    catch(e){
      console.warn(e)
    }
    finally{
      console.log(data)
    }
  }


return (
    <div className="UserNews">
      <div onLoad={haddleLoad}>
      {data && data.keys.map(r =>
        <article>
            <h2>{r?.titulo}</h2>
            <p>{r?.fecha}</p>
            <img src={r?.foto} alt={r?.foto} />
            <h4>{r?.entradilla}</h4>
            <p>{r?.tema}</p>
            <p>{r?.positivo}</p>
        </article>
      )}
      {!data && 
        <p>No hay noticias para este usuario</p>
      } 
    </div>
    </div>
)}


export default UserConf;
