import UseFetch from "../hooks/UseFetch"

function UserLogued({token}) {
const handleButton = async e => {
  try{
    const res = await fetch('http://localhost:4000/noticiasusuario', {
      method: 'GET',
      headers: {'Content-Type': 'application/json',
      'Authorization': token },
      body: JSON.stringify({ titulo: title, foto: photo, entradilla: intro, texto: text, tema: topic })
    })
    const data = await res.json()
    if (res.ok) {
      console.log(token)
      
    } else {
      setError(data?.error || 'Error desconocido')

    }
  } catch (e) {
    console.warn(e)
    setError(e.message || 'Error desconocido')
  } finally {
    setLoading(false)
  }  
}
 

return (
    <div className="News">
      {data && data.map(r =>
        <article>
            <h2>{r?.titulo}</h2>
            <p>{r?.fecha}</p>
            <img src={r?.foto} alt={r?.foto} />
            <h4>{r?.entradilla}</h4>
            <p>{r?.tema}</p>
            <p>{r?.positivo}</p>
        </article>
      )}
      {!data && 
        <p>No hay noticias en la base de datos</p>
      }
    </div>
)}

export default UserLogued;
