import './UserConf.css'
import { useState } from 'react';
import { Navbar} from 'react-router-dom'


function UserConf({token, id}) {
  console.log(token)
  console.log(id)
  const [data, setData] = useState(null)
  
  const handleButton = async e => {
    e.preventDefault()
    try{
      const res = await fetch(`http://localhost:4000/noticiasusuario/${id}`, {
        method: 'GET',
        headers: {'Content-Type': 'application/json',
        'Authorization': token},     
      })
      const data = await res.json()
        if (res.ok){
          setData(data[0])
        } 
    }   
    catch(e){
      console.warn(e)
    }
  }


return (
    <div className="UserNews">
      <button onClick={handleButton}>My articles
      <Navbar to="/userarticles"/>
      </button>
      
    </div>
)}


export default UserConf;
