import './UserConf.css'
function UserLogued({props}) {
  let data = null
const handleButton = async e => {
    const res = await fetch('http://localhost:4000/noticiasusuario', {
      method: 'GET',
      headers: {'Content-Type': 'application/json',
      'Authorization': props.token },
      body: JSON.stringify({ usuario: props.user })
    })
    data = await res.json()     
 
}
 

return (
    <div className="News">
      <button onClick={handleButton}>My news</button>
      {data && data.map(r =>
        <article>
            <h2>{r?.titulo}</h2>
            <p>{r?.fecha}</p>
            <img src={r?.foto} alt={r?.foto} />
            <h4>{r?.entradilla}</h4>
            <p>{r?.tema}</p>
            <p>{r?.positivo}</p>
        </article>
      )}
      {!data && 
        <p>No hay noticias para este usuario</p>
      }
    </div>
)}

export default UserLogued;
