import { useState, useEffect } from 'react'

const UseFetch = (url) => {
  const [data, setData] = useState(null)

  useEffect(() => {
    fetch(url)
      .then(res => res.json())
      .then(resData => setData(resData))
  }, [url])

  return data
}

export default UseFetch
