import { Link } from "react-router-dom"
import UseFetch from "../../hooks/UseFetch"
import '../../App.css'

function UserArticles({token, userId, setArticleId}) {
  const handleLink = async e => {
    e.preventDefault()
    setArticleId(newid)
    console.log(newid)
  }
  const data= UseFetch(`http://localhost:4000/noticiasusuario/${userId}`, token) 
return (
    <div className="userNews News">
      {data && data.map(r =>
        <article className="Article">
          <Link onClick={handleLink(r?.id)} className="link" to="/editarticle">
            <button> ‚úèÔ∏è</button>
          </Link>
          {r?.editado ? <i>edited</i> : <></>}
          <Link onClick={handleLink(r?.id)} className="link" to="/article">
            <h3>{r?.titulo}</h3>
          </Link>
          <i>{r?.fecha.split('T00:00:00.000Z')}</i>
          <img src={r?.foto} alt={r?.foto} />
          <h4>{r?.entradilla}</h4>
          <div>{r?.positivo} üëç &nbsp;&nbsp;&nbsp;&nbsp;
            {r?.negativo} üëé</div>
          <h5>#{r?.tema}</h5>
        </article>
      )}
    </div>
)}


export default UserArticles;
