import { useState } from "react";

import './UserConf.css'


function UserConf({token, userId}) {
  const [name, setName] = useState('')
  const [photo, setPhoto] = useState('')
  const [bio, setBio] = useState('')
  const [email, setEmail] = useState('')
  const [pass, setPass] = useState('')
  
  const handleUser = async e => {
    e.preventDefault()
    try{
      const res = await fetch('http://localhost:4000/editarusuario', {
        method: 'POST',
        headers: {'Content-Type': 'application/json',
        'Authorization': token},
        body: JSON.stringify({id:userId, nombre: name, email:email, bio:bio, foto:photo, pass:pass })
      })
      const data = await res.json()
        if (res.ok){
         console.log(JSON.stringify(data))
        } 
    }   
    catch(e){
      console.warn(e)
    }
  }


return (
    <div>
      <div>
        <h1>User data</h1>
        <form>
          <fieldset>
            <legend>
              Nombre
            </legend>
            <input name="name" value={name} onChange={e => setName(e.target.value)} />
          </fieldset>
          <fieldset>
            <legend>
              Photo
            </legend>
            <input name="photo" value={photo} onChange={e => setPhoto(e.target.value)} />
          </fieldset>
          <fieldset>
            <legend>
              Bio
            </legend>
            <input name="bio" value={bio} onChange={e => setBio(e.target.value)} />
          </fieldset>
          <fieldset>
            <legend>
              Email
            </legend>
            <input name="email" type="email" value={email} onChange={e => setEmail(e.target.value)} />
          </fieldset>
          <fieldset>
            <legend>
              Pass
            </legend>
            <input name="pass" type="password" value={pass} onChange={e => setPass(e.target.value)} />
          </fieldset>
          <button onClick={handleUser} >Change user data</button>
        </form>
      </div>
  </div>
)}


export default UserConf;
