import { Routes, Route, Link } from 'react-router-dom'
import Home from'./Home';
import Date from'./Date';
import News from'./News';
import Login from'./Login';
import Logout from'./Logout';
import Topic from'./Topic';
import { useState } from 'react';
import Signup from './Signup';
import UserArticles from './User/UserArticles';
import NewArticle from './User/NewArticle';
import UserConf from './User/UserConf';
import EditArticle from './User/EditArticle';
import ArticlePage from './ArticlePage';

function Navbar() { 
  const [token, setToken] = useState(null)
  const [userId, setUserId] = useState(null)
  const [articleId, setArticleId] = useState(null)


 return(
 <div>
  <nav>
    <Link className="link" to="/">Inicio</Link>
    <Link className="link" to="/news">News</Link>
    <Link className="link" to="/date">Date</Link>
    <Link className="link" to="/topic">Topic</Link>
    <span>
    { token ? <Link className="link" to="/logout">Logout</Link>
    : <Link className="link" to="/login">Login</Link> }
    </span>
  </nav>
  { token ? <nav className='userNavBar'>
    <Link className="link" to="/newarticle">New article</Link>
    <Link className="link" to="/userarticles">My articles</Link>
    <span>
    <Link className="link" to="/userconf">Profile</Link>
    </span>
  </nav>
  : <></>
  }
 <Routes>
   <Route path="/" exact element={<Home/>} />
   <Route path="/news" element={<News setArticleId={setArticleId}/>} />
   <Route path="/date" element={<Date setArticleId={setArticleId}/>} />
   <Route path="/topic" element={<Topic setArticleId= {setArticleId}/>} />
   <Route path="/login" element={<Login setToken = {setToken} setId={setUserId} token={token}/>} />
   <Route path="/signup" element={<Signup />} />
   <Route path="/newarticle" element={<NewArticle token= {token}/>} />
   <Route path="/userarticles" element={<UserArticles token= {token} userId= {userId} setArticleId={setArticleId}/>} />
   <Route path="/userconf" element={<UserConf token= {token} userId= {userId} />} />
   <Route path="/article" element={<ArticlePage token ={token} articleId={articleId}/>} />
   <Route path="/editarticle" element={<EditArticle token ={token} articleId={articleId}/>} />
   <Route path="/logout" element={<Logout setToken= {setToken}/>} />
 </Routes>
</div>
    
)}

export default Navbar;
