import './UserConf.css'
import UseFetch from "../../hooks/UseFetch";

function UserConf({token, user, setId, id}) {
  const data = UseFetch(`http://localhost:4000/usuarioactual/${user}`)
  setId(data[0].id)
    

const handleButton = async e => {
  e.preventDefault()
    setLoading(true)
    setError(null)
    try{
      const res = await fetch(`http://localhost:4000/noticiasusuario/${id}`, {
        method: 'GET',
        headers: {'Content-Type': 'application/json'},     
      })
      const data = await res.json()
      if (res.ok) {
        console.log(data)
      } else {
        setError(data?.error || 'Error desconocido')

      }
    } catch (e) {
      console.warn(e)
      setError(e.message || 'Error desconocido')
    } finally {
      setLoading(false)
    }  
  }
 

return (
    <div className="News">
      <button onClick={handleButton}>My news</button>
      {data && data.map(r =>
        <article>
            <h2>{r?.titulo}</h2>
            <p>{r?.fecha}</p>
            <img src={r?.foto} alt={r?.foto} />
            <h4>{r?.entradilla}</h4>
            <p>{r?.tema}</p>
            <p>{r?.positivo}</p>
        </article>
      )}
      {!data && 
        <p>No hay noticias para este usuario</p>
      }
    </div>
)}

export default UserConf;
