import { useState } from "react";
import UseFetch from "../../hooks/UseFetch";
import './UserConf.css'


function UserConf({token, id}) {
  const [name, setName] = useState('')
  const [photo, setPhoto] = useState('')
  const [bio, setBio] = useState('')
  const [email, setEmail] = useState('')
  
  //let info= UseFetch(`http://localhost:4000/noticiasusuario/${id}`, token); 
  
  /*const handleArticles = async e => {
    e.preventDefault()
    try{
      const res = await fetch(`http://localhost:4000/noticiasusuario/${id}`, {
        method: 'GET',
        headers: {'Content-Type': 'application/json',
        'Authorization': token},     
      })
      const data = await res.json()
        if (res.ok){
          info = JSON.stringify(data)
        } 
    }   
    catch(e){
      console.warn(e)
    }
    finally{
      console.log(info)
    }
  }*/

  const handleUser = async e => {
    e.preventDefault()
    try{
      const res = await fetch('http://localhost:4000/editarusuario', {
        method: 'POST',
        headers: {'Content-Type': 'application/json',
        'Authorization': token},
        body: {id:id, nombre: name, email:email, bio:bio, foto:photo }
      })
      const data = await res.json()
        if (res.ok){
         JSON.stringify(data)
        } 
    }   
    catch(e){
      console.warn(e)
    }
    finally{
      console.log(infouser)
    }
  }


return (
    <div>
      <div>
        <h1>User data</h1>
        <form>
          <fieldset>
            <legend>
              Nombre
            </legend>
            <input name="name" value={name} onChange={e => setName(e.target.value)} />
          </fieldset>
          <fieldset>
            <legend>
              Photo
            </legend>
            <input name="photo" value={photo} onChange={e => setPhoto(e.target.value)} />
          </fieldset>
          <fieldset>
            <legend>
              Bio
            </legend>
            <input name="bio" value={bio} onChange={e => setBio(e.target.value)} />
          </fieldset>
          <fieldset>
            <legend>
              Email
            </legend>
            <input name="email" type="email" value={email} onChange={e => setEmail(e.target.value)} />
          </fieldset>
          <button onClick={handleUser}>Change user data</button>
        </form>
      </div>

  </div>
)}


export default UserConf;
/**      {info && info.map(r =>
        <article>
            <h2>{r?.titulo}</h2>
            <p>{r?.fecha}</p>
            <img src={r?.foto} alt={r?.foto} />
            <h4>{r?.entradilla}</h4>
            <p>{r?.tema}</p>
            <p>{r?.positivo}</p>
        </article>
      )}
      {!info && 
        <p>No hay noticias para este usuario</p>
      }  */
