import UseFetch from "../../hooks/UseFetch";
import './UserConf.css'


function UserConf({token, id}) {
  
  const info= UseFetch(`http://localhost:4000/noticiasusuario/${id}`, token); 
  
  /*const handleArticles = async e => {
    e.preventDefault()
    try{
      const res = await fetch(`http://localhost:4000/noticiasusuario/${id}`, {
        method: 'GET',
        headers: {'Content-Type': 'application/json',
        'Authorization': token},     
      })
      const data = await res.json()
        if (res.ok){
          info = JSON.stringify(data)
        } 
    }   
    catch(e){
      console.warn(e)
    }
    finally{
      console.log(info)
    }
  }*/


return (
    <div>
      <div>
        <h1>User data</h1>
        <form>
          <fieldset>
            <legend>
              Nombre
            </legend>
            <input name="username" value={username} onChange={e => setUsername(e.target.value)} />
          </fieldset>
          <fieldset>
            <legend>
              Photo
            </legend>
            <input name="username" value={username} onChange={e => setUsername(e.target.value)} />
          </fieldset>
          <fieldset>
            <legend>
              Bio
            </legend>
            <input name="bio" value={username} onChange={e => setBio(e.target.value)} />
          </fieldset>
          <fieldset>
            <legend>
              Email
            </legend>
            <input name="email" type='email' value={email} onChange={e => setEmail(e.target.value)} />
          </fieldset>
        </form>
      </div>
      {info && info.map(r =>
        <article>
            <h2>{r?.titulo}</h2>
            <p>{r?.fecha}</p>
            <img src={r?.foto} alt={r?.foto} />
            <h4>{r?.entradilla}</h4>
            <p>{r?.tema}</p>
            <p>{r?.positivo}</p>
        </article>
      )}
      {!info && 
        <p>No hay noticias para este usuario</p>
      } 
  </div>
)}


export default UserConf;
