import UseFetch from "../hooks/UseFetch"
import PositiveVote from "./User/PositiveVote";

function News({token}) {
    const info= UseFetch('http://localhost:4000/noticiasdeldia');
    return (
      <div className="News">
        {info && info.map(r =>
          <article>
              <h2>{r?.titulo}</h2>
              <p>{r?.fecha}</p>
              <img src={r?.foto} alt={r?.foto} />
              <h4>{r?.entradilla}</h4>
              <p>{r?.tema}</p>
              <p>{r?.positivo}</p>
              {token ? <><PositiveVote>Hola</PositiveVote></>
                : <></>}
          </article>
        )}
        {!info && 
          <p>No hay noticias en la base de datos</p>
        }

      </div>
    )
  }

export default News;
