import { Link } from "react-router-dom"
import UseFetch from "../../hooks/UseFetch"
import '../../App.css'

function UserArticles({token, userId, setArticleId}) {
  function handleLink (articleId) {
    setArticleId(articleId)
  }
  const data= UseFetch(`http://localhost:4000/noticiasusuario/${userId}`, token) 
return (
    <div className="userNews News">
      {data && data.map(r =>
        <article className="Article">
          <Link onClick={handleLink(r?.id)} className="link" to="/article">
            <h3>{r?.titulo}</h3>
          </Link>
          <p>{r?.fecha}</p>
          <img src={r?.foto} alt={r?.foto} />
          <h4>{r?.entradilla}</h4>
          <p>{r?.positivo}üëç
            {r?.negativo}üëé</p>
          {r?.editado ? <p>Edited</p> : <></>}
          <h5>#{r?.tema}</h5>
          <Link onClick={handleLink(r?.id)} className="link" to="/editarticle">
            <button>‚úèÔ∏è</button>
          </Link>
        </article>
      )}
      {!data && 
        <p>No hay noticias para este usuario</p>
      } 
    </div>
)}


export default UserArticles;
