import { Link } from "react-router-dom";
import UseFetch from "../../hooks/UseFetch"

function UserArticles({token, userId, setArticleId}) {
  function handleLink (articleId) {
    setArticleId(articleId)
  }
  const data= UseFetch(`http://localhost:4000/noticiasusuario/${userId}`, token) 
return (
    <div className="userNews">
      {data && data.map(r =>
        <article>
            <Link onClick={handleLink()} className="link" to="/editarticle">
              <h3>{r?.titulo}</h3>
            </Link>
            <p>{r?.fecha}</p>
            <img src={r?.foto} alt={r?.foto} />
            <h4>{r?.entradilla}</h4>
            <p>{data?.positivo}üëç
              {data?.negativo}üëé</p>
            {r?.editado ? <p>Edited</p> : <></>}
            <h5>#{r?.tema}</h5>
            <button>Edit</button>
        </article>
      )}
      {!data && 
        <p>No hay noticias para este usuario</p>
      } 
    </div>
)}


export default UserArticles;
