import useFetch from "../hooks/useFetch"

function Topic() {
  const topic="Cultura";
    const info= useFetch(`http://localhost:4000/noticiastema/${topic}`);
    return (
      <div className="Topic">
        <form>
          <input type="text" placeholder="Tema a buscar..."></input>
          <button>Buscas</button>
        </form>
        {info && info.map(r =>
          <article>
              <h2>{r?.titulo}</h2>
              <p>{r?.fecha}</p>
              <img src={r?.foto} alt={r?.foto} />
              <h4>{r?.entradilla}</h4>
              <p>{r?.tema}</p>
              <p>{r?.positivo}</p>
          </article>
        )}
        {!info && 
          <p>No hay noticias en la base de datos</p>
        }
      </div>
    )
  }

export default Topic;
