import useFetch from "../hooks/useFetch"

function News() {
    const info= useFetch('http://localhost:4000/noticiastema');
    return (
      <div className="News">
        {info && info.map(r =>
          <article>
              <h2>{r?.titulo}</h2>
              <p>{r?.fecha}</p>
              <img src={r?.foto} alt={r?.foto} />
              <h4>{r?.entradilla}</h4>
              <p>{r?.tema}</p>
              <p>{r?.positivo}</p>
          </article>
        )}
        {!info && 
          <p>No hay noticias en la base de datos</p>
        }
      </div>
    )
  }

export default News;
