import UseFetch from "../hooks/UseFetch"
import PositiveVote from "./User/PositiveVote";
import NegativeVote from "./User/NegativeVote";

function ArticlePage({token, id}) {

    const info= UseFetch(`http://localhost:4000/noticia/${id}`);
    return (
      <div className="News">
        {info && info.map(r =>
          <article>
              <h2>{r?.titulo}</h2>
              <p>{r?.fecha}</p>
              <img src={r?.foto} alt={r?.foto} />
              <h4>{r?.texto}</h4>
              <p>{r?.tema}</p>
              <div className="votes">{ token ? 
              <><PositiveVote votes={r?.positivo} token= {token} id={r?.id}/>
              <NegativeVote votes={r?.negativo} token= {token} id={r?.id}/></>
                : <></>}</div>
          </article>
        )}
        {!info && 
          <p>No hay noticias en la base de datos</p>
        }

      </div>
    )
  }

export default ArticlePage;
