import useFetch from "../hooks/useFetch"
import './Home.css';

function Home() {
  const info = useFetch(`http://localhost:4000/`)
console.log(info);
  return (
    <main>
      <p>Noticia</p>
      <div className="noticiero">
      {info && info.map(r =>
          <article>
              <h2>{r?.titulo}</h2>
              <h3>{r?.fecha}</h3>
              <a>{r?.foto}</a>
              <h4>{r?.entradilla}</h4>
            <tr>
              <td>{r?.tema}</td>
              <td>{r?.positivo}</td>
            </tr> 
          </article>
        )
      }
      {!info && 
        <p>No hay noticias en la base de datos</p>
      }
      </div>
    </main>
  )
}

export default Home;
