import UseFetch from "../hooks/UseFetch"
import Article from "./Article";

function News() {

    const info= UseFetch('http://localhost:4000/noticiasdeldia');
    return (
      <div className="News">
          {info && info.map(r =>
          <Article data={r}/>
        )}
        {!info && 
          <p>No hay noticias en la base de datos</p>
        }
        
      </div>
    )
  }

export default News;
