import './UserConf.css'
function UserConf({token, user}) {
  let data = null
const handleButton = async e => {
  e.preventDefault()
  try{
    const res = await fetch(`http://localhost:4000/usuarioactual/:${user}`, {
      method: 'GET',
      headers: {'Content-Type': 'application/json',
      'Authorization': token }
    })
    data = await res.json()
    if(res.ok){
      console.log(data)
    }
  }
  catch(e){
    console.warn(e);
  }
        
 
}
 

return (
    <div className="News">
      <button onClick={handleButton}>My news</button>
      {data && data.map(r =>
        <article>
            <h2>{r?.titulo}</h2>
            <p>{r?.fecha}</p>
            <img src={r?.foto} alt={r?.foto} />
            <h4>{r?.entradilla}</h4>
            <p>{r?.tema}</p>
            <p>{r?.positivo}</p>
        </article>
      )}
      {!data && 
        <p>No hay noticias para este usuario</p>
      }
    </div>
)}

export default UserConf;
