import useFetch from "../hooks/useFetch"
import './Home.css';

function Home() {
  const info = useFetch(`http://localhost:4000/`)
console.log(info);
  return (
    <main>
      <p>Noticia</p>
      <div className="noticiero">
      {info && info.map(r =>
          <article>
              <h2>{r?.titulo}</h2>
              <h3>{r?.fecha}</h3>
              <img alt="cualquira">{r?.foto}</img>
              <h4>{r?.entradilla}</h4>
              <p>{r?.tema}</p>
              <p>{r?.positivo}</p>
          </article>
        )
      }
      {!info && 
        <p>No hay noticias en la base de datos</p>
      }
      </div>
    </main>
  )
}

export default Home;
