import useFetch from "../hooks/useFetch";
import './Topic.css';
import { useState } from 'react';

function Date() {
  const [date, setDate] = useState('');
  console.log(date);
    const info= useFetch(`http://localhost:4000/noticiastema/${date}`);
    return (
      <div className="Topic">
        <form>
          <fieldset>
            <legend>
              Fecha
            </legend>
            <input type="date" onChange={e => setDate(e.target.value)}></input>
          </fieldset>
        </form>
        {info && info.map(r =>
          <article>
              <h2>{r?.titulo}</h2>
              <p>{r?.fecha}</p>
              <img src={r?.foto} alt={r?.foto} />
              <h4>{r?.entradilla}</h4>
              <p>{r?.tema}</p>
              <p>{r?.positivo}</p>
          </article>
        )}
        {!info && 
          <p>No hay noticias en la base de datos</p>
        }
      </div>
    )
  }

export default Date;
