import './UserConf.css'
import UserArticles from './UserArticles';
import { useState } from 'react';


function UserConf({token, id}) {
  const [data, setData] = useState(null)
  
  const handleButton = async e => {
    e.preventDefault()
    try{
      const res = await fetch(`http://localhost:4000/noticiasusuario/${id}`, {
        method: 'GET',
        headers: {'Content-Type': 'application/json',
        'Authorization': token},     
      })
      const data = await res.json()
        if (res.ok){
          setData(data[0])
        } 
    }   
    catch(e){
      console.warn(e)
    }
  }


return (
    <div className="UserNews">
      <button onClick={handleButton}>My articles</button>
      <UserArticles data={data}/>
    </div>
)}


export default UserConf;
