import './UserConf.css'
import UseFetch from "../../hooks/UseFetch";

function UserConf({token, user, setId, id}) {
  const data = UseFetch(`http://localhost:4000/usuarioactual/${user}`, token)
  setId(data[0].id)
    

const handleButton = async e => {
  e.preventDefault()
  const res = UseFetch(`http://localhost:4000/noticiasusuario/${id}`, token)
 

return (
    <div className="News">
      <button onClick={handleButton}>My news</button>
      {data && data.map(r =>
        <article>
            <h2>{r?.titulo}</h2>
            <p>{r?.fecha}</p>
            <img src={r?.foto} alt={r?.foto} />
            <h4>{r?.entradilla}</h4>
            <p>{r?.tema}</p>
            <p>{r?.positivo}</p>
        </article>
      )}
      {!data && 
        <p>No hay noticias para este usuario</p>
      }
    </div>
)}

}

export default UserConf;
